import React, {Component} from 'react';
class Price extends Component {
    constructor (props){
    super (props)
        this.state={
            value:props.defaultValue,
            fromCurrency: "AMD",
            toCurrency: "USD"
        }
    }
    handleClick=()=>{
        if(this.state.fromCurrency!==this.state.toCurrency)
        this.setState({
            value:this.state.value*500
        });
    }
    render (){
        return (
            <div>
            <button> 
            onClick={this.handleClick}</button>
                </div>
        );
    }
}
export {Price};
